import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Button from 'react-bootstrap/Button';

export default class CarTableRow extends Component 
{
    deleteCar = () => {
        let path = this.props.history.push('/CarList');
        axios.delete('http://localhost:4000/toyota/delete-car/' + this.props.obj._id)
             .then(path)
            .catch(error => console.log(error))
    }

    editCar = () => {
        let path = '/EditCar/' + this.props.obj._id
        this.props.history.push(path)
    }

    render() {
        return (
            <tr key={this.props.obj._id}>
                <td>{this.props.obj.model}</td>
                <td>{this.props.obj.colour}</td>
                <td>{this.props.obj.year}</td>
                <td>{this.props.obj.price}</td>
                <td>
                    <Link className="green-button" to={"/EditCar/" + this.props.obj._id}>
                        Edit
                    </Link>


                     <Link className="red-button" onClick={this.deleteCar}>
                        Delete
                    </Link>                     
                </td>
            </tr>
        );
    }
}
