        let deleteRoute = 'http://localhost:4000/toyota/delete_car/' + this.props.car._id;
        axios.delete(deleteRoute)
            .then(res => this.props.history.push('/CarList'))
            .catch(error => console.log(error))
    }


    editCar = () => 
    {
        this.props.history.push('/EditCar/' + this.props.obj._id);
    }

    render() {
        return (
            <tr >
                <td>{this.props.car.model}</td>
                <td>{this.props.car.colour}</td>
                <td>{this.props.car.year}</td>
                <td>{this.props.car.price}</td>
                <td>
                    <Link className="green-button" to={"/EditCar/" + this.props.car._id}>
                        Edit
                    </Link>

                    <span className="red-button" onClick={this.deleteCar}>
                        Delete
                    </span>                     
                </td>
            </tr>
        );
    }
}
