import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import Form from "react-bootstrap/Form"

import axios from "axios";

import LinkInClass from "../components/LinkInClass";

import {ACCESS_LEVEL_ADMIN, SERVER_HOST} from "../config/global_constants"


export default class AddPerfumeReal extends Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            brand:"",
            name:"",
            description:"",
            gender:"",
            selectedFiles: null,
            products: null,
            notes: null,
            redirectToredirectToProducts: false
        }
    }

    componentDidMount()
    {
        this.inputToFocus.focus()
    }


    handleChange = (e) =>
    {
        this.setState({[e.target.name]: e.target.value})
    }

    handleFileChange = (e) =>
    {
        this.setState({selectedFiles: e.target.files})
    } 


    handleSubmit = (e) =>
    {
        e.preventDefault()

        let formData = new FormData()

        formData.append("brand", this.state.brand)
        formData.append("name", this.state.name)
        formData.append("description", this.state.description)
        formData.append("gender", this.state.gender)

        if(this.state.selectedFiles)
        {
            for(let i = 0; i < this.state.selectedFiles.length; i++)
            {
                formData.append("images", this.state.selectedFiles[i])
            }
        }

        if(this.state.products)
        {
            for(let i = 0; i < this.state.products.length; i++)
            {
                formData.append("products", this.state.products[i])
            }
        }

        if(this.state.notes)
        {
            for(let i = 0; i < this.state.notes.length; i++)
            {
                formData.append("notes", this.state.notes[i])
            }
        }


        //axios.defaults.withCredentials = true
        axios.post(`${SERVER_HOST}/products`, formData)
            .then(res =>
            {
                if(res.data)
                {
                    if (res.data.errorMessage)
                    {
                        console.log(res.data.errorMessage)
                    }
                    else
                    {
                        console.log("Record added")
                        this.setState({redirectToProducts:true})
                    }
                }
                else
                {
                    console.log("Record not added")
                }
            })



    }
    render()
    {
        return (
            <div className="form-container">
                {this.state.redirectToProducts ? <Redirect to="/Products"/> : null}

                <Form>
                    <Form.Group controlId="model">
                        <Form.Label>Model</Form.Label>
                        <Form.Control ref = {(input) => { this.inputToFocus = input }} type="text" name="model" value={this.state.model} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="colour">
                        <Form.Label>Colour</Form.Label>
                        <Form.Control type="text" name="colour" value={this.state.colour} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="year">
                        <Form.Label>Year</Form.Label>
                        <Form.Control type="text" name="year" value={this.state.year} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="price">
                        <Form.Label>Price</Form.Label>
                        <Form.Control type="text" name="price" value={this.state.price} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="photos">
                        <Form.Label>Photos</Form.Label>
                        <Form.Control
                            type = "file" multiple onChange = {this.handleFileChange}
                        /></Form.Group> <br/><br/>

                    <LinkInClass value="Add" className="green-button" onClick={this.handleSubmit}/>

                    <Link className="red-button" to={"/DisplayAllCars"}>Cancel</Link>
                </Form>
            </div>
        )

    }
}
