import React, {Component} from "react"
import {Redirect, Link} from "react-router-dom"
import Form from "react-bootstrap/Form"

import axios from "axios"

import LinkInClass from "../components/LinkInClass"

import {SERVER_HOST} from "../config/global_constants"

export default class Register extends Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            name:"",
            username:"",
            password:"",
            confirmPassword:"",
            isRegistered:false
        }
    }


    handleChange = (e) =>
    {
        this.setState({[e.target.name]: e.target.value})
    }


    handleSubmit = (e) =>
    {
        e.preventDefault()

        const userObject = {
            name: this.state.name,
            username: this.state.username,
            password: this.state.password
        }

        console.log(userObject);

        axios.defaults.withCredentials = true
        axios.post(`${SERVER_HOST}/user/Register/${this.state.name}/${this.state.username}/${this.state.password}`, userObject)
            .then(res =>
            {
                if(res.data)
                {
                    console.log(res.data.accessLevel)
                    

                    if (res.data.errorMessage)
                    {
                        console.log(res.data.accessLevel)
                        console.log(res.data.errorMessage)
                    }
                    else
                    {
                        console.log("User Registered and added")
                        sessionStorage.name = res.data.name
                        sessionStorage.accessLevel = res.data.accessLevel

                        this.setState({isRegistered:true})
                    }
                }
                else
                {
                    console.log("Registration failed")
                }
            })
    }

    validateName()
    {
        // valid name pattern
        return (this.state.name.length > 1)
    }


    validateUsername()
    {
        // valid email pattern
        return (this.state.username)
    }


    validatePassword()
    {
        const pattern = /^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[£!#€$%^&*]).{10,}$/   //eslint-disable-line
        return pattern.test(String(this.state.password))
    }


    validateConfirmPassword()
    {
        return ((this.state.confirmPassword.length > 0) && (this.state.password === this.state.confirmPassword))
    }


    validate()
    {
        return {
            name: this.validateName(),
            username: this.validateUsername(),
            password: this.validatePassword(),
            confirmPassword: this.validateConfirmPassword()
        }
    }


    render()
    {

        const checkMark = "✔"
        const formInputsState = this.validate()
        const inputsAreAllValid = Object.keys(formInputsState).every(index => formInputsState[index])

        let nameErrorMessage = "";
        let usernameErrorMessage = "";
        let passwordErrorMessage = "";
        let confirmPasswordErrorMessage = "";

        if(!this.validateName())
        {
            nameErrorMessage = <div className="error">Name must be at least two characters long<br/></div>;
        }

        if(!this.validateUsername())
        {
            usernameErrorMessage = <div className="error">Enter a valid username<br/></div>;
        }

        if(!this.validatePassword())
        {
            let errorMessages = [];
            if(this.state.password.length < 10)
            {
                errorMessages.push("Password must be at least ten characters long.");
            }
            if(!this.state.password.match(/[0-9]/))
            {
                errorMessages.push("Password must contain at least one digit (0-9).");
            }
            if(!this.state.password.match(/[a-z]/))
            {
                errorMessages.push("Password must contain at least one lowercase character.");
            }
            if(!this.state.password.match(/[A-Z]/))
            {
                errorMessages.push("Password must contain at least one uppercase character.{'\n''}");
            }
            if(!this.state.password.match(/[£!#€$%^&*]/))
            {
                errorMessages.push("Password must contain at least one of the characters £!#€$%^&*");
            }
            passwordErrorMessage = <div className="error"><ul>{errorMessages.map(errorMessage => <li key={errorMessage}> {errorMessage} </li>)}</ul></div>;
        }

        if(!this.validateConfirmPassword())
        {
            confirmPasswordErrorMessage = <div className="error">Passwords must match<br/></div>;
        }



        return (
            /*<div className="form-container">
                {this.state.redirectToDisplayAllCars ? <Redirect to="/DisplayAllCars"/> : null}

                <Form>
                    <Form.Group controlId="name">
                        <Form.Label>Name</Form.Label>
                        <Form.Control ref = {(input) => { this.inputToFocus = input }} type="text" name="name" value={this.state.name} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="email">
                        <Form.Label>Email</Form.Label>
                        <Form.Control type="text" name="email" value={this.state.email} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="password">
                        <Form.Label>Password</Form.Label>
                        <Form.Control type="text" name="password" value={this.state.password} onChange={this.handleChange} />
                    </Form.Group>

                    <LinkInClass value="Add" className="green-button" onClick={this.handleSubmit}/>

                    <Link className="red-button" to={"/DisplayAllCars"}>Cancel</Link>
                </Form>
            </div>*/

            /*<form className="form-container" noValidate = {true} id = "loginOrRegistrationForm" onSubmit={this.handleSubmit}>

                {this.state.isRegistered ? <Redirect to="/DisplayAllCars"/> : null}

                <h2>New User Registration</h2>

                <input
                    name = "name"
                    type = "text"
                    placeholder = "Name"
                    autoComplete="name"
                    value = {this.state.name}
                    onChange = {this.handleChange}
                    ref = {(input) => { this.inputToFocus = input }}
                /><br/>

                <input
                    name = "username"
                    type = "text"
                    placeholder = "Username"
                    autoComplete="username"
                    value = {this.state.username}
                    onChange = {this.handleChange}
                /><br/>

                <input
                    name = "password"
                    type = "password"
                    placeholder = "Password"
                    autoComplete="password"
                    title = "Password must be at least ten-digits long and contains at least one lowercase letter, one uppercase letter, one digit and one of the following characters (£!#€$%^&*)"
                    value = {this.state.password}
                    onChange = {this.handleChange}
                /><br/>

                <input
                    name = "confirmPassword"
                    type = "password"
                    placeholder = "Confirm password"
                    autoComplete="confirmPassword"
                    value = {this.state.confirmPassword}
                    onChange = {this.handleChange}
                /><br/><br/>

                <LinkInClass value="Register New User" className="green-button" onClick={this.handleSubmit} />
                <Link className="red-button" to={"/DisplayAllCars"}>Cancel</Link>
            </form>

*/


            <form className="form-container" noValidate = {true} id = "loginOrRegistrationForm" onSubmit={this.handleSubmit}>

                {this.state.isRegistered ? <Redirect to="/DisplayAllCars"/> : null}

                <h2>New User Registration</h2>

                <div id="nameContainer">
                    <input
                        name = "name"
                        type = "text"
                        placeholder = "Name"
                        autoComplete="name"
                        value = {this.state.name}
                        onChange = {this.handleChange}
                        ref = {(input) => { this.inputToFocus = input }}
                    />
                    <span className={!this.validateName() ?"" : "showCorrectTick"}>{checkMark}</span>
                </div>
                {nameErrorMessage}

                <div id="usernameContainer">
                    <input
                        name = "username"
                        type = "text"
                        placeholder = "Username"
                        autoComplete="username"
                        value = {this.state.username}
                        onChange = {this.handleChange}
                    />
                    <span className={!this.validateUsername() ?"" : "showCorrectTick"}>{checkMark}</span>
                </div>
                {usernameErrorMessage}

                <div id="passwordContainer">
                    <input
                        name = "password"
                        type = "password"
                        placeholder = "Password"
                        autoComplete="password"
                        title = "Password must be at least ten-digits long and contains at least one lowercase letter, one uppercase letter, one digit and one of the following characters (£!#€$%^&*)"
                        value = {this.state.password}
                        onChange = {this.handleChange}
                    />
                    <span className={!this.validatePassword() ?"" : "showCorrectTick"}>{checkMark}</span>
                </div>
                {passwordErrorMessage}

                <div id="passwordContainer">
                    <input
                        name = "confirmPassword"
                        type = "password"
                        placeholder = "Confirm password"
                        autoComplete="confirmPassword"
                        value = {this.state.confirmPassword}
                        onChange = {this.handleChange}
                    />
                    <span className={!this.validateConfirmPassword() ?"" : "showCorrectTick"}>{checkMark}</span>
                </div>
                {confirmPasswordErrorMessage}

                <input
                    type = "submit"
                    value = "Register"
                    disabled = {!inputsAreAllValid}
                />

                <Link className="red-button" to={"/DisplayAllCars"}>Cancel</Link>
            </form>


        )
    }
}
