import { Link } from 'react-router-dom';
import axios from 'axios';

export default class CarTableRow extends Component 
{
    constructor(props)
    {
        super(props)
        this.state={car:this.props.car}
    }


    deleteCar = () => {
        axios.delete('http://localhost:4000/toyota/delete_car/' + this.props.obj._id)
            .then(res => {
                this.setState({car:null});
        this.props.history.push('/carList')})
            .catch(error => console.log(error))
    }



    editCar = () => {
        let path = '/EditCar/' + this.props.obj._id
        this.setState({car:null});
        this.props.history.push(path)
    }

    render() {
        return (
            <tr >
                <td>{this.props.car.model}</td>
                <td>{this.props.car.colour}</td>
                <td>{this.props.car.year}</td>
                <td>{this.props.car.price}</td>
                <td>
                    <Link className="green-button" to={"/EditCar/" + this.props.car._id}>
                        Edit
                    </Link>


                     <Link className="red-button" onClick={this.deleteCar}>
                        Delete
                    </Link>                     
                </td>
            </tr>
        );
    }
}
