import React, { Component } from "react";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default class CreateCar extends Component {

  constructor(props) {
    super(props)

    // Setting up functions
    this.onChangeCarName = this.onChangeCarName.bind(this);
    this.onChangeCarEmail = this.onChangeCarEmail.bind(this);
    this.onChangeCarRollno = this.onChangeCarRollno.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    // Setting up state
    this.state = {
      name: '',
      email: '',
      rollno: ''
    }
  }

  onChangeCarName(e) {
    this.setState({ name: e.target.value })
  }

  onChangeCarEmail(e) {
    this.setState({ email: e.target.value })
  }

  onChangeCarRollno(e) {
    this.setState({ rollno: e.target.value })
  }

  onSubmit(e) {
    e.preventDefault()

    const toyotaObject = {
      name: this.state.name,
      email: this.state.email,
      rollno: this.state.rollno
    };

    axios.post('http://localhost:4000/d01234567/create-toyota', toyotaObject)
      .then(res => console.log(res.data));

    this.setState({
      name: '',
      email: '',
      rollno: ''
    });
  }

  render() {
    return (<div className="form-wrapper">
      <Form onSubmit={this.onSubmit}>
        <Form.Group controlId="Name">
          <Form.Label>Name</Form.Label>
          <Form.Control type="text" value={this.state.name} onChange={this.onChangeCarName} />
        </Form.Group>

        <Form.Group controlId="Email">
          <Form.Label>Email</Form.Label>
          <Form.Control type="email" value={this.state.email} onChange={this.onChangeCarEmail} />
        </Form.Group>

        <Form.Group controlId="Name">
          <Form.Label>Roll No</Form.Label>
          <Form.Control type="text" value={this.state.rollno} onChange={this.onChangeCarRollno} />
        </Form.Group>

        <Button variant="danger" size="lg" block="block" type="submit">
          Create Car
        </Button>
      </Form>
    </div>);
  }
}
