import React, { Component } from "react";
import { Redirect, Link } from "react-router-dom";
import Form from "react-bootstrap/Form"

import axios from "axios";

import LinkInClass from "../components/LinkInClass";

import {ACCESS_LEVEL_ADMIN, SERVER_HOST} from "../config/global_constants"
//import products from "../../../server/models/products";


export default class AddPerfumeReal extends Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            brand:"",
            name:"",
            description:"",
            gender:"",
            selectedFiles: null,
            products: [{
                size : "",
                price : "",
                stockLevel : ""
                }],
            notes: {
                topNotes : ["pepper", "yuzu", "nutmeg"],
                heartNotes : ["amber", "geranium"],
                stockLevel : ["Oud"]
                },
            redirectToredirectToProducts: false
        }
    }

    /*
                {
                size : {type: Number, required:true},
	            price : {type: Number, required:true},
	            stockLevel : {type: Number, required:true}
                }
    */

    componentDidMount()
    {
        this.inputToFocus.focus()
    }


    handleChange = (e) =>
    {
        this.setState({[e.target.name]: e.target.value})
    }

    handleFileChange = (e) =>
    {
        this.setState({selectedFiles: e.target.files})
    } 


    handleChangeSize = (e) =>
        {
            let target = e.target.name;
            console.log(target)
            let products = {...this.state.products};
            products[0].size = e.target.value;
            this.setState({products})

            console.log(products)

        }

        handleChangePrice = (e) =>
        {
            let target = e.target.name;
            console.log(target)
            let products = {...this.state.products};
            products[0].price = e.target.value;
            this.setState({products})

            console.log(products)

        }

        handleChangeStock = (e) =>
        {
            let target = e.target.name;
            console.log(target)
            let products = {...this.state.products};
            products[0].stockLevel = e.target.value;
            this.setState({products})

            console.log(products)

        }


        handleProductChange = (e) =>
        {
            let target = e.target.name;
            console.log(target)
            let products = {...this.state.products};

            if(target === "size")
            {
                products[0].size = e.target.value;
            }
            else if (target === "price")
            {
                products[0].price = e.target.value;
            }
            else
            {
                products[0].stockLevel = e.target.value;
            }

            this.setState({products})

            console.log(products)
        }
    



   /* hardCodedproduct()
    {
        let productsContainer = {
            size : 12,
            price : 10,
            stockLevel : 130
            };

            this.setState({products: [productsContainer]})
    }

    hardCodednotes()
    {
        let notesContainer = {
            topNotes : ["pepper", "yuzu", "nutmeg"],
            heartNotes : ["amber", "geranium"],
            stockLevel : ["Oud"]
            };

            this.setState({notes: [notesContainer]})
    }*/

    handleSubmit = (e) =>
    {
        e.preventDefault()

        //this.hardCodedproduct()
        //this.hardCodednotes()
        
        /*let productsContainer = [{
            size : 12,
            price : 10,
            stockLevel : 130
            }];

            let notesContainer = [{
                topNotes : ["pepper", "yuzu", "nutmeg"],
                heartNotes : ["amber", "geranium"],
                stockLevel : ["Oud"]
                }];*/
           

        let formData = new FormData()

        


        formData.append("brand", this.state.brand)
        formData.append("name", this.state.name)
        formData.append("description", this.state.description)
        formData.append("gender", this.state.gender)
    
       
         

        if(this.state.selectedFiles)
        {
            for(let i = 0; i < this.state.selectedFiles.length; i++)
            {
                formData.append("productsPhotos", this.state.selectedFiles[i])
            }
        }

        if(this.state.products)
        {
            for(let i = 0; i < this.state.products.length; i++)
            {
                            formData.append("products", this.state.products[i])
            }
        }

        if(this.state.notes)
        {
            for(let i = 0; i < this.state.notes.length; i++)
            {
                            formData.append("notes", this.state.notes[i])
            }
        }

         const valuez = [...formData.entries()]
  
         console.log(valuez)


        //axios.defaults.withCredentials = true
        axios.post(`${SERVER_HOST}/products`, formData, {headers:{"Content-type": "multipart/form-data"}})
            .then(res =>
            {

                console.log("111111111111111")

                if(res.data)
                {

                    

                    if (res.data.errorMessage)
                    {

                        console.log("IN HERE")
                        console.log(res.data.errorMessage)
                    }
                    else
                    {
                        console.log("Record added")
                        this.setState({redirectToProducts:true})
                    }
                }
                else
                {
                    console.log("Record not added")
                }
            }).catch(error => {
                console.log("Error caught in catch block")
                console.log(error)
                }
              )



    }
    render()
    {
        return (
            <div className="form-container">
                {this.state.redirectToProducts ? <Redirect to="/Products"/> : null}

                <Form>
                    <Form.Group controlId="brand">
                        <Form.Label>Brand</Form.Label>
                        <Form.Control type="text" name="brand" value={this.state.brand} onChange={this.handleChange} />
                    </Form.Group>

                   <Form.Group controlId="name">
                        <Form.Label>Name</Form.Label>
                        <Form.Control ref = {(input) => { this.inputToFocus = input }} type="text" name="name" value={this.state.name} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="description">
                        <Form.Label>Description</Form.Label>
                        <Form.Control type="text" name="description" value={this.state.description} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="gender">
                        <Form.Label>Gender</Form.Label>
                        <Form.Control type="text" name="gender" value={this.state.gender} onChange={this.handleChange} />
                    </Form.Group>

                    <Form.Group controlId="productsPhotos">
                        <Form.Label>Product Photos</Form.Label>
                        <Form.Control
                            type = "file" multiple onChange = {this.handleFileChange}
                        /></Form.Group> 
                        
                    <Form.Group controlId="size">
                        <Form.Label>Size</Form.Label>
                        <Form.Control type="text" name="size" value={this.state.products[0].size} onChange={this.handleProductChange} />
                    </Form.Group>


                    <Form.Group controlId="price">
                        <Form.Label>Price</Form.Label>
                        <Form.Control type="number" name="price" value={this.state.products[0].price} onChange={this.handleProductChange} />
                    </Form.Group>

                    
                    <Form.Group controlId="stockLevel">
                        <Form.Label>Stock Available</Form.Label>
                        <Form.Control type="number" name="stockLevel" value={this.state.products[0].stockLevel} onChange={this.handleProductChange} />
                    </Form.Group>
                  
                        
                        
                        
                        
                        
                        
                        <br/><br/>



                    <LinkInClass value="Add" className="green-button" onClick={this.handleSubmit}/>

                    
                </Form>
            </div>
        )

    }
}
