import React, { Component } from "react";
import Form from 'react-bootstrap/Form'
        import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default class CreateCar extends Component
{
    constructor(props)
    {
        super(props)

        this.state = {
            model: '',
            colour: '',
            year: '',
            price: ''
        }
    }

    handleChange = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }

    handleSubmit = (e) => {
        e.preventDefault()

        const carObject = {
            model: this.state.model,
            colour: this.state.colour,
            year: this.state.year,
            price: this.state.price
        };

        axios.post('http://localhost:4000/toyota/create-car', carObject)
                .then(res => console.log(res.data));

        this.setState({
            model: '',
            colour: '',
            year: '',
            price: ''
        });

        return  <Redirect  to='/car-list' />

    }

    render()
    {
        return (<div classModel="form-wrapper">
        <Form onSubmit={this.handleSubmit}>
            <Form.Group controlId="Name">
                <Form.Label>Model</Form.Label>
                <Form.Control type="text" name="model" value={this.state.model} onChange={this.handleChange} />
            </Form.Group>

            <Form.Group controlId="Name">
                <Form.Label>Colour</Form.Label>
                <Form.Control type="text" name="colour" value={this.state.colour} onChange={this.handleChange} />
            </Form.Group>

            <Form.Group controlId="Name">
                <Form.Label>Year</Form.Label>
                <Form.Control type="text" name="year" value={this.state.year} onChange={this.handleChange} />
            </Form.Group>

            <Form.Group controlId="Name">
                <Form.Label>Price</Form.Label>
                <Form.Control type="text" name="price" value={this.state.price} onChange={this.handleChange} />
            </Form.Group>

            <Button variant="danger" size="lg" block="block" type="submit">
                Create Car
            </Button>
        </Form>
    </div>);
    }
}
