      }).catch((error) => {
        console.log(error)
      })

    // Redirect to Car List 
    this.props.history.push('/car-list')
  }


  render() {
    return (<div classModel="form-wrapper">
      <Form onSubmit={this.handleSubmit}>
        <Form.Group controlId="Name">
          <Form.Label>Model</Form.Label>
          <Form.Control type="text" name="model" value={this.state.model} onChange={this.handleChange} />
        </Form.Group>

        <Form.Group controlId="Name">
          <Form.Label>Colour</Form.Label>
          <Form.Control type="text" name="colour" value={this.state.colour} onChange={this.handleChange} />
        </Form.Group>

        <Form.Group controlId="Name">
          <Form.Label>Year</Form.Label>
          <Form.Control type="text" name="year" value={this.state.year} onChange={this.handleChange} />
        </Form.Group>

        <Form.Group controlId="Name">
          <Form.Label>Price</Form.Label>
          <Form.Control type="text" name="price" value={this.state.price} onChange={this.handleChange} />
        </Form.Group>

        <Button variant="danger" size="lg" block="block" type="submit">
          Update Car
        </Button>
      </Form>
    </div>);
  }
}
