import React, { Component } from "react";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button';
import axios from 'axios';

export default class EditCar extends Component {

  constructor(props) {
    super(props)

    this.onChangeCarModel = this.onChangeCarModel.bind(this);
    this.onChangeCarColour = this.onChangeCarColour.bind(this);
    this.onChangeYear = this.onChangeYear.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    // State
    this.state = {
      model: '',
      colour: '',
      year: '',
      price: ''
    }
  }

  componentDidMount() {
    axios.get('http://localhost:4000/toyota/edit-car/' + this.props.match.params.id)
      .then(res => {
        this.setState({
          model: res.data.model,
          colour: res.data.colour,
          year: res.data.year,
          price: res.data.price
        });
      })
      .catch((error) => {
        console.log(error);
      })
  }

  onChangeCarModel(e) {
    this.setState({ model: e.target.value })
  }

  onChangeCarColour(e) {
    this.setState({ colour: e.target.value })
  }

  onChangeYear(e) {
    this.setState({ year: e.target.value })
  }

  onChangePrice(e) {
    this.setState({ price: e.target.value })
  }

  onSubmit(e) {
    e.preventDefault()

    const carObject = {
      model: this.state.model,
      colour: this.state.colour,
      year: this.state.year,
      price: this.state.price
    };

    axios.put('http://localhost:4000/toyota/update-car/' + this.props.match.params.id, carObject)
      .then((res) => {
        console.log(res.data)
        console.log('Car successfully updated')
      }).catch((error) => {
        console.log(error)
      })

    // Redirect to Car List 
    this.props.history.push('/car-list')
  }


  render() {
    return (<div classModel="form-wrapper">
      <Form onSubmit={this.onSubmit}>
        <Form.Group controlId="Name">
          <Form.Label>Model</Form.Label>
          <Form.Control type="text" value={this.state.model} onChange={this.onChangeCarModel} />
        </Form.Group>

        <Form.Group controlId="Colour">
          <Form.Label>Colour</Form.Label>
          <Form.Control type="text" value={this.state.colour} onChange={this.onChangeCarColour} />
        </Form.Group>

        <Form.Group controlId="Name">
          <Form.Label>Year</Form.Label>
          <Form.Control type="text" value={this.state.year} onChange={this.onChangeYear} />
        </Form.Group>

        <Form.Group controlId="Name">
          <Form.Label>Price</Form.Label>
          <Form.Control type="text" value={this.state.price} onChange={this.onChangePrice} />
        </Form.Group>

        <Button variant="danger" size="lg" block="block" type="submit">
          Update Car
        </Button>
      </Form>
    </div>);
  }
}
