import React, { Component } from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';
import CarTable from './CarTable';


export default class ListAllCars extends Component 
{
  constructor(props) 
  {
    super(props)
    this.state = {
         cars: []
         };    

    // timer is needed to allow the database change to take effect before getting the results
    setTimeout(() => {axios.get('http://localhost:4000/cars/')
                           .then(res => this.setState({cars: res.data}))
                           .catch((error) => console.log(error))}, 200); // wait 500 milliseconds
  }


  render() {
    return (
            <div className="table-container">
              <CarTable cars={this.state.cars} />

              <div className="add-new-car">
                <Link className="blue-button" to={"/AddCar"}>Add New Car</Link>
              </div>
            </div>
    );
  }
}
