import React, { Component } from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';
import Table from 'react-bootstrap/Table';
import CarTableRow from './CarTableRow';


export default class CarList extends Component {

  constructor(props) {
    super(props)
    this.state = {
      cars: []
    };
  }


  DataTable() {
      axios.get('http://localhost:4000/toyota/')
      .then(res => {
        this.setState({
          cars: res.data
        });
        console.log("hi")
                console.log(res.data)

      })
      .catch((error) => {
        console.log(error);
      })


    return this.state.cars.map(car => {
      return <CarTableRow obj={car} key={car._id} />;
    });
  }


  render() {
    return (<div className="table-wrapper">
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Model</th>
            <th>Colour</th>
            <th>Year</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {this.DataTable()}
        </tbody>
      </Table>
      <Link className="edit-link" to={"/create-car"}>
                  Add
                </Link>

    </div>);
  }
}
