        return pattern.test(String(this.state.password))
    }


    validateConfirmPassword()
    {
        return ((this.state.confirmPassword.length > 0) && (this.state.password === this.state.confirmPassword))
    }


    validate()
    {
        return {
            name: this.validateName(),
