import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Button from 'react-bootstrap/Button';

export default class CarTableRow extends Component 
{
    deleteCar = () => {
        axios.delete('http://localhost:4000/toyota/delete-car/' + this.props.obj._id)
            .then((res) => {
                console.log('Car successfully deleted!')

         // Redirect to Car List 
        this.props.history.push('/car-list')

            }).catch((error) => {
                console.log(error)
            })
    }

    editCar = () => {
        let path = '/edit-car/' + this.props.obj._id
        this.props.history.push(path)
    }

    render() {
        return (
            <tr key={this.props.obj._id}>
                <td>{this.props.obj.model}</td>
                <td>{this.props.obj.colour}</td>
                <td>{this.props.obj.year}</td>
                <td>{this.props.obj.price}</td>
                <td>
                    <Link className="edit-link" to={"/edit-car/" + this.props.obj._id}>
                        Edit
                    </Link>


                     <Link className="delete-link" onClick={this.deleteCar}>
                        Delete
                    </Link>

                      <Button variant="success" size="lg" block="block" onClick={this.editCar}>
                Edit
            </Button>
                </td>
            </tr>
        );
    }
}
